import cv2import customtkinter as ctkimport easyocrdef image(pos):    print("image detected")    print(pos)    text=" "    reader = easyocr.Reader(['en'],verbose=False)    img=cv2.imread(pos)    cv2.imshow("real",img)    img_gray=cv2.cvtColor(img,cv2.COLOR_BGR2GRAY)    cv2.imshow("gray",img_gray)    _,img_thresh=cv2.threshold(img_gray,30,255,cv2.THRESH_TOZERO)    cv2.imshow("thresh",img_thresh)    result=reader.readtext(img_gray)    text=result[0][1]    text_score=result[0][2]    if len(text)==0:        retry(pos,text_score)    elif len(text)!=0:        if text_score>0.80 and len(text)>=6:            print("most accurate result")            print("plate :",text)        else:            print("less accurate result")            retry(pos,text_score)    else:        print("can't detect try with more clear image")    cv2.waitKey(0)def detect(but,pat):    if but=="image detection":        image(pat)def retry(gfg,text_sc):    reader2 = easyocr.Reader(['en'],verbose=False)    img2=cv2.imread(gfg)    cv2.imshow("real",img2)    img_gray2=cv2.cvtColor(img2,cv2.COLOR_BGR2GRAY)    cv2.imshow("gray",img_gray2)    _,img_thresh2=cv2.threshold(img_gray2,30,255,cv2.THRESH_TOZERO)    cv2.imshow("thresh",img_thresh2)    result1=reader2.readtext(img_thresh2)    text2=result1[0][1]    text_score2=result1[0][2]    if len(text2)==0:        print("can't detect")    else:        if text_score2>text_sc:            print("plate :",text2)        else:            pass    cv2.waitKey(0)def change_label(event):    print(event)    if event=="live-video":        url_lable.configure(text="wifi-url")    elif event=="video":        url_lable.configure(text="path of video")    elif event=="image":        url_lable.configure(text="path of image")priyanshu=ctk.CTk()priyanshu.title("TOLL-XPERT")priyanshu.geometry("400x500")priyanshu.minsize(400,500)priyanshu.maxsize(400,500)priyanshi=ctk.CTkFrame(priyanshu,fg_color="pink")priyanshi.pack(fill="both",expand=True)title_tag=ctk.CTkLabel(priyanshi,text="TOLL-XPERT",text_color="yellow",font=('calibre',34,'normal'),width=400,height=50)title_tag.place(x=0,y=0)priyanshi1=ctk.CTkFrame(priyanshi,fg_color="hotpink",width=370,height=100)priyanshi1.place(x=10,y=50)empty_label=ctk.CTkLabel(priyanshi1,text=" ",height=30)empty_label.grid(row=0,column=0)clicked=ctk.StringVar()clicked.set("-----------------------------------|select|---------------------------------")option_menu=ctk.CTkOptionMenu(priyanshi1,values=['image detection','video capture','live-video'],dropdown_hover_color="orange",variable=clicked,command=change_label,width=370)option_menu.grid(padx=5,row=1,column=1,columnspan=2)url_lable=ctk.CTkLabel(priyanshi1,text_color="black")url_lable.grid(row=4,column=1)url_lable.configure(text="Path")pather=ctk.StringVar()location_entry=ctk.CTkEntry(priyanshi1,width=200,height=30,textvariable=pather)location_entry.grid(row=4,column=2)location_entry.bind('<Return>',lambda:detect(clicked.get(),pather.get()))detect_button=ctk.CTkButton(priyanshi,text="Detect",width=200,height=40,hover_color="lime",command=lambda:detect(clicked.get(),pather.get()))detect_button.place(x=100,y=150)dark_frame=ctk.CTkFrame(priyanshi,fg_color="black",width=400,height=250)dark_frame.place(x=0,y=200)delete_button=ctk.CTkButton(priyanshi,text="Clear",width=400,height=50,hover_color="lime")delete_button.place(x=0,y=450)priyanshu.mainloop()