import cv2import easyocrimport timecount=0reader=easyocr.Reader(['en'],gpu=True,verbose=False)result1=0cap=cv2.VideoCapture("C:/Users/Admin/Downloads/Untitled design (1).mp4")#starting time to be captureda=time.time()print("start : ",a)print("/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-|||loading|||-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/-/")#initiating loop untill frame is stopedwhile(True):    ret,img=cap.read()    img_gray=cv2.cvtColor(img,cv2.COLOR_BGR2GRAY)    _,img_thresh2=cv2.threshold(img_gray,125,255,cv2.THRESH_TOZERO)    result=reader.readtext(img_gray)    text=result[0][1]    text_score=result[0][2]    if len(text)==0:        print("retry required")    if text!=result1 and len(text)>=5 and text_score>0.6:        result1=text        count=count+1        print("number plate",count,":",text)        print("accuracy",count,text_score)    #cv2.imshow("out",img_thresh2)    if cv2.waitKey(1) & 0xFF == ord("d"):        breakprint("stop time :",time.time())cap.release()cv2.destroyAllWindows()